import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import React, { useState } from 'react';
import People from "./People";
import PeopleComponent from './componenst/PeopleComponent';
import Form from './componenst/form';
import Lemmikud from './Lemmikud';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

function App() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [people, setPeople] = React.useState(People);

  const addItem = (newItem) => {
    setPeople(prevPeople => [...prevPeople, newItem]);
  };

  const addEdited = (id, newData) => {
    const newArray = people.map(person =>
      person.id === id ? { ...newData } : person
    );
    setPeople(newArray);
  };

  const Delete = (id) => {
    setPeople(people.filter(person => person.id !== id));
  }

  return (
    <div className="App">
      <Router>
        <nav>
          <ul>
            <li>
              <Link to="/Lemmikud">Lemmikud</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/Lemmikud" element={<Lemmikud />} />
        </Routes>
        <div className="container">
          {people && (
            <div className="row">
              {people.map((person, index) => (
                <div className="col-md-4" key={index}>
                  <PeopleComponent
                    picture={person.picture}
                    phone={person.phone}
                    education={person.education}
                    name={person.name}
                    id={person.id}
                    addEdited={addEdited}
                    Delete={Delete}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
        <Form addItem={addItem} />
      </Router>
    </div>
  );
}

export default App;
