import MainContent from './componenst/MainContent';
import data from './componenst/data';
import './App.css';
import React, { useState, useEffect } from 'react';
import boxes from './componenst/boxes';
import BoxSisu from './componenst/boxSisu';
import BoxSisu2 from './BoxSisu2';
import Isikud from './Isikud';
import jobs from './jobs';


function App() {

  /*  const sisu = data.map((item, index) => (
     <MainContent
       key={index}
       name={item.name}
       text={item.text}
       date={item.date}
       img={item.img}
       data={data}
     />
   )); */
  const lisa = () => {
    return (
      <div>
        {data.map((item, index) => (
          <div key={index}>
            <p>{item.name}</p>
            <p>{item.date}</p>
          </div>
        ))}
      </div>
    );
  };
  const [count, setCount] = React.useState(0)

  const add = () => {
    setCount(prevCount => prevCount === 5 ? 10 : prevCount + 1);
  }
  const subs = () => {
    setCount(count - 1)
  }
  const [qst, setQst] = React.useState("yes")
  let muuda = () => {
    setQst(prevQst => prevQst === "yes" ? "no" : "yes")
  }

  const [kalakutta, setKalakutta] = React.useState([
    { id: "kalakutta1", name: "Item 1" },
    { id: "kalakutta2", name: "Item 2" },
    { id: "kalakutta3", name: "Item 3" },
    { id: "kalakutta4", name: "Item 4" },
    { id: "kalakutta5", name: "Item 5" }
  ]);


  const Lisa = () => {
    setKalakutta(prev => {
      const newItem = {
        id: `kalakutta ${kalakutta.length + 1}`,
        item: `Item ${kalakutta.length + 1}`
      }
      return [...prev, newItem]
    })
  }

  const deleteItem = (id) => {
    setKalakutta(prev => prev.filter(item => item.id !== id));
  }

  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    // Function to fetch weather data
    const fetchWeatherData = async () => {
      try {
        const response = await fetch('https://api.open-meteo.com/v1/forecast?latitude=52.52&longitude=13.41&hourly=temperature_2m,wind_speed_10m&timezone=Europe%2FMoscow');
        const data = await response.json();
        setWeatherData(data);
      } catch (error) {
        console.error('Error fetching weather data:', error);
      }
    };

    // Call fetchWeatherData function
    fetchWeatherData();
  }, []);

  const DeleteItem = (id) => {
    setWeatherData(prev => prev.filter(item => item.id !== id));
  }

  const [data, setData] = useState(
    {
      name: 'ints',
      age: 41,
      working: true
    });

  const picture = data.working ? "/images/1.jpg" : "/images/2.jpg";

  const toggle = () => {
    setData(prevData => ({
      ...prevData,
      working: !prevData.working
    }));
  }

  const style = {
    backgroundColor: "",
    margin: "3px",
    height: "60px",
    width: "60px",
    display: "flex",
    flexDirextion: "column"
  }

  const vajuta = (id) => {
    setBox(prevBox => {
      return prevBox.map(item => item.id === id ? { ...item, on: !item.on } : item)

    })

  }

  const [box, setBox] = useState(boxes)

  const [isShown, setIsShown] = useState(true)
  const toggleShown = () => {
    return setIsShown(prev => !prev)
  }

  const [persons, setPersons] = useState(Isikud)
  const [jobPlaces, setjobPlaces] = useState(jobs)

  const displayWorking = (id) => {
    setA(prevA => !prevA)
    setPersons(prev => prev.map((person) => {

      return person.id === id ? { ...person, working: !person.working } : person;
    }))

  }

  const DelItem = (id) => {
    setPersons(prev => prev.filter(person => person.id !== id))
  }
  const [a, setA] = useState(true)
  const [messages, setMessages] = useState([a, b])

  return (

    <div className="App">
      {messages && <p>"you have messages"</p>}
      <button onClick={toggleShown}>Show/hide</button>
      {isShown &&
        <p> {/* contitional rendering starts here */}
          <ul>
            {persons && persons.map((person, index) => {
              return <li >{person.name}
                {person.phone}
                {person.address}
                <p
                  style={{ display: person.working ? 'block' : 'none' }}>{person.phone}
                </p><button onClick={() => displayWorking(person.id)}>show working</button><button onClick={() => DelItem(person.id)}> {a && "delete" || "add"}</button>
                <p style={{ backgroundColor: "#708090", color: "#FFFAFA" }}>{jobPlaces[index].jobs}</p>
              </li>
            })
            }
          </ul>
        </p>
      }
      {box.map((boxItem, index) => (
        <div key={boxItem.id}>
          <BoxSisu2
            on={boxItem.on}
            id={boxItem.id}
            vajuta={vajuta}
            time={weatherData && Array.isArray(weatherData.hourly.time) ? weatherData.hourly.time[index] : "no data"}
          />
        </div>
      ))}
      {/*  <BoxSisu
            on={box.on}
            id={box.id}
            vajuta={vajuta}
          /> */}


      <img src={picture} alt="a picture" onClick={toggle} />
      <button onClick={Lisa}>Add Item</button>

      {kalakutta.map(item => {
        return (
          <div key={item.item} id={item.id}>
            <p>{item.item}</p>
            <button onClick={() => DeleteItem(item.id)}>Delete</button>
          </div>
        )
      })}


      <div>
        Calculator
        <p onClick={muuda}>{count}</p>
        <button onClick={add}>Add</button>
        <button onClick={subs}>substract</button>
      </div>
      {weatherData ? (
        <div>
          {weatherData.hourly.time.map((item, index) => ({
            id: index, // or any unique identifier for the item
            name: item
          })).map(({ id, name }) => (
            <div key={id}>
              <p>{name}</p>
              <button onClick={() => deleteItem(id)}>Delete</button>
            </div>
          ))}
        </div>
      ) : (<p>No data!</p>)
      }


    </div>

  );

}

export default App;
