import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Routes, Route, HashRouter } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './index.css';
import App from './App';
import Lemmikud from './Lemmikud';
import reportWebVitals from './reportWebVitals';
import People from './People';
import React, { useState } from 'react';
import Folders from './Folders';


const AppWithPeople = () => {
  const [people, setPeople] = useState(People);
  const [isLiked, setIsLiked] = useState([]);
  const [LikeButtonColor, setLikeButtonColor] = useState(true)
  const [folders, setFolders] = useState([])
  const [AddtofolderToggle, setAddtofolderToggle] = useState(true)
  const [buttonColor, setButtonColor] = useState(false)

  const toggleLike = (id) => {
    setPeople(prevPeople => {
      const updatedPeople = prevPeople.map(person =>
        person.id === id ? { ...person, like: !person.like } : person
      );
      // Update the isLiked state array based on the updated people array
      const likedIds = updatedPeople.filter(person => person.like)
      setIsLiked(likedIds);
      setLikeButtonColor(prev => !prev)

      return updatedPeople;
    });
  };

  const removeFromFavorites = (id) => {
    setPeople(prevPeople => (
      prevPeople.map(person => (
        person.id === id ? { ...person, like: !person.like } : person
      ))
    ))
    setIsLiked(prev => prev.filter(item => item.id !== id)
    )
  }

  const addToFolder = (folderId, personId) => {
    /* const img = picture instanceof File ? URL.createObjectURL(picture) : picture;
    const folderToUpdate = folders.find(folder => folder.id === folderId); */
    setButtonColor(!buttonColor)
    console.log(people)
  }
  return (
    <React.StrictMode>
      <HashRouter>
        <Routes>
          <Route path="/" element={<App people={people} setPeople={setPeople} isLiked={isLiked}
            setIsLiked={setIsLiked} toggleLike={toggleLike} LikeButtonColor={LikeButtonColor}
            setLikeButtonColor={setLikeButtonColor} folders={folders} setFolders={setFolders} addToFolder={addToFolder}
            AddtofolderToggle={AddtofolderToggle} setAddtofolderToggle={setAddtofolderToggle} buttonColor={buttonColor} />} />

          <Route path="/Lemmikud" element={<Lemmikud people={people} setPeople={setPeople}
            isLiked={isLiked} removeFromFavorites={removeFromFavorites} />} />

          <Route path="/Folders" element={<Folders people={people} setPeople={setPeople}
            isLiked={isLiked} removeFromFavorites={removeFromFavorites} folders={folders} setFolders={setFolders}
          />} />
        </Routes>
      </HashRouter>
    </React.StrictMode>
  );
}

ReactDOM.render(
  <AppWithPeople />,
  document.getElementById('root')
);

