import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import React, { createContext, useState, useContext } from 'react';
import PeopleComponent from './componenst/PeopleComponent';
import Form from './componenst/form';
import Lemmikud from './Lemmikud';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import People from "./People";
import Context from "./context";


function App({ people, setPeople }) {
  const [isModalOpen, setIsModalOpen] = useState(false);


  const addItem = (newItem) => {
    setPeople(prevPeople => [...prevPeople, newItem]);
  };

  const addEdited = (id, newData) => {
    const newArray = people.map(person =>
      person.id === id ? { ...newData } : person
    );
    setPeople(newArray);
  };

  const Delete = (id) => {
    setPeople(people.filter(person => person.id !== id));
  }

  return (
    <Context.Provider>
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <div className="container">
            <Link className="navbar-brand" to="/">Home</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav">
                <li className="nav-item">
                  <Link className="nav-link" to="/Lemmikud">Lemmikud</Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>

        <div className="container">
          {people && (
            <div className="row">
              {people.map((person, index) => (
                <div className="col-md-4" key={index}>
                  <PeopleComponent
                    picture={person.picture}
                    phone={person.phone}
                    education={person.education}
                    name={person.name}
                    id={person.id}
                    addEdited={addEdited}
                    Delete={Delete}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
        <Form addItem={addItem} />

      </div>

    </Context.Provider>

  );
}
