import React from 'react';

const Form = ({ addItem }) => {
    const [data, setData] = React.useState({
        name: "", phone: "", education: "", picture: ""
    });

    const handleChange = (e) => {
        setData(prevData => ({
            ...prevData,
            [e.target.name]: e.target.value
        }));
    };



    const handleFileChange = (e) => {
        setData(prevData => ({
            ...prevData,
            picture: e.target.files[0] // Update the image property with the selected file
        }));
    };

    /*  const handleSubmit = (e) => {
         e.preventDefault();
         addItem(data);
         setData({
             name: "", phone: "", education: "", picture: ""
         });
     };
  */
    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        placeholder='Enter your name'
                        onChange={handleChange}
                        name="name"
                        value={data.name}
                    />
                    <input
                        type="text"
                        className="form-control"
                        id="phone"
                        placeholder='Enter your phone'
                        onChange={handleChange}
                        name="phone"
                        value={data.phone}
                    />
                    <input
                        type="text"
                        className="form-control"
                        id="education"
                        placeholder='Enter your education'
                        onChange={handleChange}
                        name="education"
                        value={data.education}
                    />

                    <input
                        type="file"
                        className="form-control"
                        id="file"
                        onChange={handleFileChange}
                        name="image"

                    />
                </div>
                <button className="btn btn-primary" onClick={handleAddItem}>Submit</button>
                {/* <button type="submit" className="btn btn-primary">Submit</button> */}
            </form>
        </div>
    );
};

export default Form;
