import React from 'react';
import Card from 'react-bootstrap/Card';
import EditModal from './EditModal';
import { useState } from 'react';

const PeopleComponent = (props) => {
    const [like, setLike] = useState(true);
    /*  const [isModalOpen, setIsModalOpen] = useState(false); */

    const handleLike = () => {
        setLike(prev => !prev);
    };

    const openModal = () => {
        props.setIsModalOpen(prev => !prev);
    };

    const closeModal = () => {
        props.setIsModalOpen(prev => !prev);
    };

    return (
        <>
            <div className="card" style={{ marginTop: "20px" }}>
                <div style={{ position: 'relative' }}>
                    <img src={props.picture instanceof File ? URL.createObjectURL(props.picture) : props.picture} className="card-img-top" />
                    <img src={like ? "./images/like1.jpg" : "./images/like.jpg"}
                        style={{
                            position: 'absolute',
                            bottom: '5px',
                            left: '5px',
                            height: "20px",
                            width: "20px"
                        }}
                        alt="like-icon"
                        onClick={handleLike}
                    />
                    <button onClick={openModal}>Edit</button>
                </div>
                <div className="card-body">
                    <h5 className="card-title">{props.name}</h5>
                    <p className="card-text">Phone: {props.phone}</p>
                    <p className="card-text">Education: {props.education}</p>
                </div>
            </div>
            <EditModal isOpen={props.isModalOpen}
                onClose={closeModal}
                namePlaceholder={props.name}
                phonePlaceholder={props.phone}
                educationPlaceholder={props.education}
                addEdited={props.addEdited}
                id={props.id}
            />
        </>
    );
};

export default PeopleComponent;
