import React from 'react';
import Card from 'react-bootstrap/Card';
import EditModal from './EditModal';
import { useState, useRef, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import { useLocation } from 'react-router-dom';
const [AddtofolderToggle, setAddtofolderToggle] = useState(true)



const PeopleComponent = (props) => {                 //  komponent, mis kasutab väärttuste kuvamiseks  props. 
    const [like, setLike] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);  // state mis kontrolli modal avamist ja sulgemist, vastavalt boolean  väärtusele true or false 



    const openModal = () => {
        setIsModalOpen(prev => !prev); //  modal avamiseks
    };

    const closeModal = () => {
        setIsModalOpen(prev => !prev);// modal sulgemiseks 
    };


    const addToFolder = (folderId, personId) => {

        // Define variables
        const img = props.picture instanceof File ? URL.createObjectURL(props.picture) : props.picture;
        const folderToUpdate = props.folders.find(folder => folder.id === folderId);

        if (props.AddtofolderToggle) {
            // Add the item to the folder
            const updatedFolder = folderToUpdate ? {
                ...folderToUpdate,
                image: [...folderToUpdate.image, { id: personId, phone: props.phone, img: img }]
            } : null;
            const updatedFolders = props.folders.map(folder =>
                folder.id === folderId ? updatedFolder : folder
            );
            props.setFolders(updatedFolders);
            console.log(props.folders);
        } else {
            // Remove the item from the folder
            const updatedFolders = props.folders.map(folder => ({
                ...folder,
                image: folder.image.filter(item => item.id !== personId)
            }));
            props.setFolders(updatedFolders);
            console.log(props.folders);
        }
        // Toggle the state
        setAddtofolderToggle(!AddtofolderToggle);
    };


    /*   const addToFolder = (folderId, personId) => {
    const img = props.picture instanceof File ? URL.createObjectURL(props.picture) : props.picture;
      // leiame folderi, mis vastab folderid -le
      const folderToUpdate = props.folders.find(folder => folder.id === folderId);
      const updatedFolder = folderToUpdate ? {
          ...folderToUpdate,
          image: [...folderToUpdate.image, { id: personId, phone: props.phone, img: img }] // Add person's phone to image array
      } : null;
      // Update the folders array in state with the updated folder object
      const updatedFolders = props.folders.map(folder =>
          folder.id === folderId ? updatedFolder : folder
      );
      // Update state with the updated folders array
      props.setFolders(updatedFolders);
      console.log(props.folders)
      setAddtofolderToggle(!AddtofolderToggle)} */


    return (
        <>
            <div className="card" style={{ marginTop: "20px" }} >
                <div style={{ position: 'relative' }}>

                    <img src={props.picture instanceof File ? URL.createObjectURL(props.picture) : props.picture} className="card-img-top" />


                    <button
                        style={{
                            backgroundColor: '#343a40',
                            color: '#f8f9fa',
                            border: '1px solid #343a40',
                            marginRight: '10px',
                            padding: '6px 12px',
                            textAlign: 'center',
                            textDecoration: 'none',
                            display: 'inline-block',
                            fontSize: '16px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            margin: '3px'
                        }}
                        onClick={openModal}
                    >Edit
                    </button> {/* // funktsioon mis ava modal i (andmete uuendamine/updateimine) */}
                    <button
                        style={{
                            backgroundColor: '#343a40',
                            color: '#f8f9fa',
                            border: '1px solid #343a40',
                            marginRight: '10px',
                            padding: '6px 12px',
                            textAlign: 'center',
                            textDecoration: 'none',
                            display: 'inline-block',
                            fontSize: '16px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            margin: '3px'
                        }}
                        onClick={() => props.Delete(props.id)}>
                        Delete
                    </button>
                    <button
                        style={{
                            backgroundColor: props.like ? '#4FC3F7' : '#343a40',
                            color: '#f8f9fa',
                            border: '1px solid #343a40',
                            marginRight: '10px',
                            padding: '6px 12px',
                            textAlign: 'center',
                            textDecoration: 'none',
                            display: 'inline-block',
                            fontSize: '16px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            margin: '3px'
                        }}
                        onClick={() => props.toggleLike(props.id)}
                    >{props.like ? "Liked" : "Like"}
                    </button>
                </div>
                <div className="card-body">
                    <h5 className="card-title">{props.name}</h5>
                    <p className="card-text">Phone: {props.phone}</p>
                    <p className="card-text">Education: {props.education}</p>
                </div>

                <div style={{ backgroundColor: '#ffffff' }}>
                    <p style={{ backgroundColor: '#343a40', color: "white" }}>Add item to folder <h3>+</h3> </p>
                    {props.folders && props.folders.map((item) => (
                        <button
                            style={{
                                backgroundColor: props.AddtofolderToggle ? " #5EB19C" : '#E5C539',
                                color: "white",
                                margin: "3px, 4px, 6px"
                            }}
                            className="btn  mr-2"
                            onClick={() => addToFolder(item.id, props.id)}
                            key={item.id}
                        >
                            {item.name}
                        </button>

                    ))}
                </div>

            </div >
            <EditModal isOpen={isModalOpen} /*  Modali komponent , annan props, kaasa , samuti ka addEdited(),  mis asub  App.js is */
                onClose={closeModal}
                namePlaceholder={props.name}
                phonePlaceholder={props.phone}
                educationPlaceholder={props.education}
                addEdited={props.addEdited}
                id={props.id}

            />
        </>
    );
};

export default PeopleComponent;
