import { Modal, Card } from 'react-bootstrap';
import React, { useRef } from 'react';

const SearcModal = ({ show, onHide, searchFilter }) => {
    const inputRef = useRef(null);

    function focusInput() {
        inputRef.current.focus();
    }
    return (
        <Modal show={show} onHide={onHide}>
            <Modal.Header closeButton>
                <Modal.Title>Search Results</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                {searchFilter.map((item, index) => (
                    <Card key={index} style={{ width: '18rem', marginBottom: '1rem' }}>
                        <Card.Img variant="top" src={item.picture} />
                        <Card.Body>
                            <Card.Title>{item.name}</Card.Title>
                            <Card.Text>
                                Phone: {item.phone}<br />
                                Education: {item.education}
                            </Card.Text>
                            <button onClick={focusInput} className="btn btn-dark rounded-pill text-light">
                                Go to original for edit or delete!
                            </button>
                        </Card.Body>
                    </Card>
                ))}

            </Modal.Body>
        </Modal>
    );
}

export default SearcModal;
