import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import React, { useState } from 'react';
import People from "./People";                      // impordin andmes sisse komponendist People, salvestan state väärktuseks [people]
import PeopleComponent from './componenst/PeopleComponent';
import Form from './componenst/form';
import Lemmikud from './Lemmikud';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';


function App() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const [people, setPeople] = React.useState(People)

  const addItem = (newItem) => {                      // teen argumentidega funktsiooni et lisada uus item array se people!
    setPeople(prevPeople => [...prevPeople, newItem]);// kasutan spread operaatorit  uued itemi lismiseks
  };

  const addEdited = (id, newData) => {
    const newArray = people.map(person =>             // funktsioon  mis kasutab sisendeid id ja newData itereerib people array d. 
      person.id === id ? { ...newData } : person      // funktsioon käib läbi people array ja kui tuvastab sama id mis on sisendis siis selle Itemi kirjutab 
    );                                                 //üle uue data ga. Uueks dataks saab  komponendis EdidModali formi täitmiselt tekkiv info(või ehk.  data)
    setPeople(newArray);                               // kuna map() tagastab meile uue array, siis peame people State muutma uueks arrayks. (people array
  };                                                    // on pea array, mida kuvatakse 

  const Delete = (id) => {
    console.log(id)
    setPeople(people.filter(person => person.id !== id));
  }
  //KUTSTUTAB ITEMI ARRAYST 
  return (
    <>
      <Router>
        <div className="App">

          <Routes>
            <Route path="/" element={<App />} />
            <Route path="/Lemmikud" element={<Lemmikud />} />
          </Routes>

          <nav>
            <ul>
              <li>
                <Link to="/Lemmikud">Lemmikud</Link>
              </li>
            </ul>
          </nav>

          <div className="container">
            {people && (                                    //  people && tähendab  conditional render, kui state muutuja omab sisu ehkt on (true), 
              <div className="row">                         {/* arrayde puhul tähendb array liikmete olemas olu tagastust true, igal index kohal arrays. */}
                {people.map((person, index) => (
                  <div className="col-md-4" key={index}>   {/*   iterreerime üle people array ja igal kohal arrays  renderdame <PeopleComponendi />, millele anname kaasa 
                                                         //props muutujad, nt phone={person.phone}  */}
                    <PeopleComponent
                      picture={person.picture}
                      phone={person.phone}
                      education={person.education}
                      name={person.name}
                      id={person.id}
                      addEdited={addEdited}
                      Delete={Delete}


                    />
                  </div>
                ))}
              </div>
            )}
          </div>
          <Form
            addItem={addItem}                      /*     kuvame komponendi <Form /> , anname kaas props. muutujana funktsiooni addItem(). mida kirjeldasin üleval*/
          />                                        {/* ilmselt oleksin saanud kasutada ühte form komponenti nii <EditModal/> is kui ka <Form /> */}



        </div >
      </Router>
    </>
  );

}

export default App;
