import React, { Fragment, useState } from 'react'
import { Button } from 'react-bootstrap'
import NavBar from './Nav'

const Folders = ({ people, setPeople, isLiked, removeFromFavorites, folders, setFolders }) => {

    const randomNumber = Math.floor(Math.random() * 1000000) + 1;
    const [newFolder, setNewFolder] = useState(
        {
            id: randomNumber,
            name: "",
            image: [],
            description: "",
            visibility: false
        }
    )

    const greateDirectory = (e) => {
        e.preventDefault();

        // Create a new folder object with the current state values
        const newFolderToAdd = {
            id: newFolder.id,
            name: newFolder.name,
            description: newFolder.description,
            visibility: newFolder.visibility,
            image: [], // Initialize the image array
        };

        // Update the folders state using a callback function
        setFolders(prevFolders => [...prevFolders, newFolderToAdd]);

        // Reset the newFolder state
        setNewFolder({
            id: randomNumber,
            name: "",
            description: "",
            visibility: false,
        });
    };





    const handleChange = (e) => {    /* onChange funktsiooni formis info kogumiseks newFolder state i.  */
        setNewFolder(prev => ({
            ...prev,                /* onChange update ib iga sisse typeitud tähe/sümboliga antud välja objektis */
            [e.target.name]: e.target.value
        }));

    };
    const handleVisibility = (itemId) => {
        setFolders(prevFolders =>
            prevFolders.map(item =>
                item.id === itemId ? { ...item, visibility: !item.visibility } : item
            )
        );
    };
    console.log(folders)


    return (

        <div className="container">
            <NavBar />
            <form onSubmit={greateDirectory}>
                <div className="mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        placeholder='Enter folder name'
                        onChange={handleChange}
                        name="name"
                        value={newFolder.name}
                    />
                    <input
                        type="text"
                        className="form-control"
                        id="description"
                        placeholder='Enter some description'
                        onChange={handleChange}
                        name="description"
                        value={newFolder.description}
                    />

                </div>
                <div className="row">
                    <div className="col">
                        <button type="submit" className="btn btn-primary mr-2">Greate</button>
                    </div>
                </div>


            </form>

            {folders && folders.map((item) => (
                <div className="border border-secondary bg-light shadow-sm my-2 pb-0" key={item.id}>
                    <p
                        className="mb-0 text-light text-uppercase d-flex align-items-center"
                        style={{ cursor: "pointer", backgroundColor: "black" }}
                        onClick={() => handleVisibility(item.id)}
                    >
                        FOLDER: {item.name}
                    </p>

                    {/*  renderdan update-tud folders, kus on ses juba uus item väärtused pealehelt */}
                    {item.visibility && (
                        <div className="border p-2">
                            <p className="mb-0">{item.description}</p>
                            {item.image.length > 0 && (
                                <>{/* siin pean map() läbi item.image arra[], (tee ära kui koju tuled ohtul) , tehtud*/}
                                    {/* <img src={item.image[0].img} alt="Image" /> ajutiselt välja commitud*/}

                                    {item.image.length > 0 && (
                                        <div className="d-flex align-items-center">
                                            {item.image.map((unit, index) => (
                                                <>
                                                    <img
                                                        src={unit.img}
                                                        alt="Image"
                                                        key={index}
                                                        style={{
                                                            marginRight: '10px',
                                                            width: '300px',
                                                            height: 'auto'
                                                        }}
                                                    />


                                                </>


                                            ))}
                                        </div>
                                    )}



                                </>
                            )}
                        </div>
                    )}
                </div>

            ))}

        </div>

    )
}

export default Folders
