import React from 'react';

const Form = (props) => {


    /*   const [random, setRandom] = useState(null);
  
      // Filter the people array to check if the generated random number already exists
      const existingIds = People.map(person => person.id);
      const isRandomValid = !existingIds.includes(random);
  
      // Generate a new random number if it doesn't exist in the people array
      if (!random || !isRandomValid) {
          const newRandom = Math.floor(Math.random() * 1000000) + 1;
          setRandom(newRandom);
      } */




    const [data, setData] = React.useState({
        name: "", phone: "", education: "", picture: "", id: undefined
    });

    const handleChange = (e) => {
        setData(prevData => ({
            ...prevData,
            [e.target.name]: e.target.value
        }));
    };



    const handleFileChange = (e) => {
        setData(prevData => ({
            ...prevData,
            picture: e.target.files[0]
        }));
    };
    const randomNumber = Math.floor(Math.random() * 1000000) + 1;
    const handleSubmit = (e) => {
        console.log(data)
        e.preventDefault();

        props.addItem({ data });
        setData({
            name: "", phone: "", education: "", picture: "", id: randomNumber
        });
    };

    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        placeholder='Enter your name'
                        onChange={handleChange}
                        name="name"
                        value={data.name}
                    />
                    <input
                        type="text"
                        className="form-control"
                        id="phone"
                        placeholder='Enter your phone'
                        onChange={handleChange}
                        name="phone"
                        value={data.phone}
                    />
                    <input
                        type="text"
                        className="form-control"
                        id="education"
                        placeholder='Enter your education'
                        onChange={handleChange}
                        name="education"
                        value={data.education}
                    />

                    <input
                        type="file"
                        className="form-control"
                        id="file"
                        onChange={handleFileChange}
                        name="image"

                    />
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    );
};

export default Form;
