import React, { useState } from 'react'
import { Button } from 'react-bootstrap'

const Folders = ({ people, setPeople, isLiked, removeFromFavorites, folders, setFolders }) => {
    const randomNumber = Math.floor(Math.random() * 1000000) + 1;

    const greateDirectory = (e) => {

        console.log(folders)
        e.preventDefault();
        setFolders(prevFolders => [...prevFolders, newFolder, newFolder.id = randomNumber]);
        setNewFolder({
            id: "", name: "", image: "", description: "", visibility: true, id: ""//tühjendab väljad
        });
    }
    const [newFolder, setNewFolder] = useState(
        {
            id: "",
            name: "",
            image: [],
            description: "",
            visibility: true
        }
    )



    const handleChange = (e) => {    /* onChange funktsiooni formis info kogumiseks newFolder state i.  */
        setNewFolder(prev => ({
            ...prev,                /* onChange update ib iga sisse typeitud tähe/sümboliga antud välja objektis */
            [e.target.name]: e.target.value
        }));
        console.log(newFolder)
    };
    return (


        <div className="container">
            <form onSubmit={greateDirectory}>
                <div className="mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        placeholder='Enter folder name'
                        onChange={handleChange}
                        name="name"
                        value={newFolder.name}
                    />
                    <input
                        type="text"
                        className="form-control"
                        id="description"
                        placeholder='Enter some description'
                        onChange={handleChange}
                        name="description"
                        value={newFolder.description}
                    />

                </div>
                <div className="row">
                    <div className="col">
                        <button type="submit" className="btn btn-primary mr-2">Greate</button>
                    </div>
                </div>


            </form>

        </div>

    )
}

export default Folders
