import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Routes, Route, HashRouter } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './index.css';
import App from './App';
import Lemmikud from './Lemmikud';
import reportWebVitals from './reportWebVitals';
import People from './People';
import React, { useState } from 'react';

const AppWithPeople = () => {
  const [people, setPeople] = useState(People);
  const [isLiked, setIsliked] = useState([]);

  const toggleLike = (id) => { // functsioon mis liidab Liked itemid Lemmiku componenti(renderdan isLiked array sinna)
    setPeople(prevPeople => {
      return prevPeople.map(person => {
        if (person.id === id) {
          const updatedPerson = { ...person, like: !person.like };
          // Update isLiked state
          if (!isLiked.includes(updatedPerson.id)) {
            setIsLiked(prevLiked => [...prevLiked, updatedPerson.id]);
          } else {
            setIsLiked(prevLiked => prevLiked.filter(item => item !== updatedPerson.id));
          }
          return updatedPerson;
        }
        return person;
      });
    });
  };


  return (
    <React.StrictMode>
      <HashRouter>
        <Routes>
          <Route path="/" element={<App people={people} setPeople={setPeople} isLiked={isLiked} setIsliked={setIsliked} />} />
          <Route path="/Lemmikud" element={<Lemmikud people={people} setPeople={setPeople} />} />
        </Routes>
      </HashRouter>
    </React.StrictMode>
  );
}

ReactDOM.render(
  <AppWithPeople />,
  document.getElementById('root')
);

reportWebVitals();
