// EditModal.js

import React from 'react';

const EditModal = (props) => {

    return (
        <div className={`modal ${props.isOpen ? 'show' : ''}`} tabIndex="-1" role="dialog" style={{ display: props.isOpen ? 'block' : 'none' }}>
            <div className="modal-dialog" role="document">
                <div className="modal-content" style={{ backgroundColor: '#001F3F' }}> {/* Set modal background color */}
                    <div className="modal-header">
                        <h5 className="modal-title" style={{ color: '#ffffff' }}>Edit Details</h5> {/* Set header text color */}
                        <button type="button" className="close" onClick={props.onClose}>
                            <span style={{ color: '#ffffff' }}>&times;</span> {/* Set close icon color */}
                        </button>
                    </div>
                    <div className="modal-body">
                        <form>
                            <div className="form-group">
                                <label htmlFor="name" style={{ color: '#ffffff' }}>Name:</label> {/* Set label text color */}
                                <input type="text" className="form-control" id="name" name="name" style={{ borderColor: '#ffffff', color: '#ffffff' }} /> {/* Set input border and text color */}
                            </div>
                            <div className="form-group">
                                <label htmlFor="phone" style={{ color: '#ffffff' }}>Phone:</label>
                                <input type="text" className="form-control" id="phone" name="name" style={{ borderColor: '#ffffff', color: '#ffffff' }} />
                            </div>
                            <div className="form-group">
                                <label htmlFor="education" style={{ color: '#ffffff' }}>Education:</label>
                                <input type="text" className="form-control" id="education" name="education" style={{ borderColor: '#ffffff', color: '#ffffff' }} />
                            </div>
                            <div className="form-group">
                                <label htmlFor="picture" style={{ color: '#ffffff' }}>Picture:</label>
                                <input type="file" className="form-control-file" id="picture" style={{ borderColor: '#ffffff', color: '#ffffff' }} />
                            </div>
                        </form>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" onClick={props.onClose}>Close</button>
                        <button type="button" className="btn btn-primary">Save Changes</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EditModal;
