import React, { useState } from 'react'
import { Button } from 'react-bootstrap'
import NavBar from './Nav'

const Folders = ({ people, setPeople, isLiked, removeFromFavorites, folders, setFolders }) => {

    const randomNumber = Math.floor(Math.random() * 1000000) + 1;
    const [newFolder, setNewFolder] = useState(
        {
            id: randomNumber,
            name: "",
            image: [],
            description: "",
            visibility: true
        }
    )

    const greateDirectory = (e) => {

        console.log(folders)
        e.preventDefault();


        setFolders(prevFolders => [...prevFolders, newFolder]);
        setNewFolder({
            id: randomNumber, name: "", image: "", description: "", visibility: true,
        });
    }




    const handleChange = (e) => {    /* onChange funktsiooni formis info kogumiseks newFolder state i.  */
        setNewFolder(prev => ({
            ...prev,                /* onChange update ib iga sisse typeitud tähe/sümboliga antud välja objektis */
            [e.target.name]: e.target.value
        }));
        console.log(newFolder)
    };
    const handleVisibility = (itemId) => {
        setFolders(prevFolders =>
            prevFolders.map(item =>
                item.id === itemId ? { ...item, visibility: !item.visibility } : item
            )
        );
    };

    return (


        <div className="container">
            <NavBar />
            <form onSubmit={greateDirectory}>
                <div className="mb-3">
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        placeholder='Enter folder name'
                        onChange={handleChange}
                        name="name"
                        value={newFolder.name}
                    />
                    <input
                        type="text"
                        className="form-control"
                        id="description"
                        placeholder='Enter some description'
                        onChange={handleChange}
                        name="description"
                        value={newFolder.description}
                    />

                </div>
                <div className="row">
                    <div className="col">
                        <button type="submit" className="btn btn-primary mr-2">Greate</button>
                    </div>
                </div>


            </form>

            {folders && folders.map((item) => (
                <div className="border my-2 pb-0" key={item.id}>
                    <p
                        className="mb-0"
                        style={{ cursor: "pointer" }}
                        onClick={() => handleVisibility(item.id)}

                    >
                        {item.name}

                    </p>{/*  renderdan update-tud folders, kus on ses juba uus item väärtused pealehelt */}
                    {item.visibility && (
                        <div className="border p-2">
                            <p className="mb-0">{item.description}</p>
                            {item.image.length > 0 && (
                                <>
                                    <p className="mb-0">{item.image[0].phone}</p>
                                    <img src={item.image[0].img} alt="Image" />
                                </>
                            )}
                        </div>
                    )}
                </div>

            ))}

        </div>

    )
}

export default Folders
