import React from 'react';
import Card from 'react-bootstrap/Card';
import EditModal from './EditModal';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';

const PeopleComponent = (props) => {                 // tüüpiline komponent, mis kasutab väärttuste kuvamiseks  props. 
    const [like, setLike] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);  // state mis kontrolli modal avamist ja sulgemist, vastavalt boolean  väärtusele true or false 



    const openModal = () => {
        setIsModalOpen(prev => !prev); //  modal avamiseks
    };

    const closeModal = () => {
        setIsModalOpen(prev => !prev);// modal sulgemiseks 
    };

    return (
        <>
            <div className="card" style={{ marginTop: "20px" }} ref={props.inputRef}>
                <div style={{ position: 'relative' }}>

                    <img src={props.picture instanceof File ? URL.createObjectURL(props.picture) : props.picture} className="card-img-top" />

                    <button
                        style={{
                            backgroundColor: '#343a40',
                            color: '#f8f9fa',
                            border: '1px solid #343a40',
                            marginRight: '10px',
                            padding: '6px 12px',
                            textAlign: 'center',
                            textDecoration: 'none',
                            display: 'inline-block',
                            fontSize: '16px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            margin: '3px'
                        }}
                        onClick={openModal}
                    >Edit
                    </button> {/* // funktsioon mis ava modal i (andmete uuendamine/updateimine) */}
                    <button
                        style={{
                            backgroundColor: '#343a40',
                            color: '#f8f9fa',
                            border: '1px solid #343a40',
                            marginRight: '10px',
                            padding: '6px 12px',
                            textAlign: 'center',
                            textDecoration: 'none',
                            display: 'inline-block',
                            fontSize: '16px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            margin: '3px'
                        }}
                        onClick={() => props.Delete(props.id)}>
                        Delete
                    </button>
                    <button
                        style={{
                            backgroundColor: props.like ? '#4FC3F7' : '#343a40',
                            color: '#f8f9fa',
                            border: '1px solid #343a40',
                            marginRight: '10px',
                            padding: '6px 12px',
                            textAlign: 'center',
                            textDecoration: 'none',
                            display: 'inline-block',
                            fontSize: '16px',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            margin: '3px'
                        }}
                        onClick={() => props.toggleLike(props.id)}
                    >{props.like ? "Liked" : "Like"}
                    </button>
                </div>
                <div className="card-body">
                    <h5 className="card-title">{props.name}</h5>
                    <p className="card-text">Phone: {props.phone}</p>
                    <p className="card-text">Education: {props.education}</p>
                </div>
            </div>
            <EditModal isOpen={isModalOpen} /*  Modali komponent , annan props, kaasa , samuti ka addEdited(),  mis asub  App.js is */
                onClose={closeModal}
                namePlaceholder={props.name}
                phonePlaceholder={props.phone}
                educationPlaceholder={props.education}
                addEdited={props.addEdited}
                id={props.id}
                focusInput={props.focusInput}
            />
        </>
    );
};

export default PeopleComponent;
