import React from 'react';
import Card from 'react-bootstrap/Card';
import EditModal from './EditModal';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';

const PeopleComponent = (props) => {                 // tüüpiline komponent, mis kasutab väärttuste kuvamiseks  props. 
    const [like, setLike] = useState(true);
    const [isModalOpen, setIsModalOpen] = useState(false);  // state mis kontrolli modal avamist ja sulgemist, vastavalt boolean  väärtusele true or false 

    const handleLike = () => {
        setLike(prev => !prev);   //like dislike kontroll funktsioon ,  toggle süsteemiga muudab true to false ja vastupidi
    };

    const openModal = () => {
        setIsModalOpen(prev => !prev); //  modal avamiseks
    };

    const closeModal = () => {
        setIsModalOpen(prev => !prev);// modal sulgemiseks 
    };

    return (
        <> {/* BOOTSTRAP CARD */}
            <div className="card" style={{ marginTop: "20px" }}>
                <div style={{ position: 'relative' }}>
                    {/* {pildi aadress vastavalt sisse arvutist sisse laetud failile. } */}
                    <img src={props.picture instanceof File ? URL.createObjectURL(props.picture) : props.picture} className="card-img-top" />
                    <img src={like ? "./images/like1.jpg" : "./images/like.jpg"} // LIKE DISLIKE(valed, suvalised  pildid hetkel)
                        style={{
                            position: 'absolute',
                            bottom: '5px',
                            left: '5px',
                            height: "20px",
                            width: "20px"
                        }}
                        alt="like-icon"
                        onClick={handleLike}

                    />                                  {/* like dialike lõpp*/}

                    <button onClick={openModal}>Edit</button> {/* // funktsioon mis ava modal i (andmete uuendamine/updateimine) */}
                    <button onClick={() => props.Delete(props.id)}>Delete</button>
                </div>
                <div className="card-body">
                    <h5 className="card-title">{props.name}</h5>
                    <p className="card-text">Phone: {props.phone}</p>
                    <p className="card-text">Education: {props.education}</p>
                </div>
            </div>
            <EditModal isOpen={isModalOpen} /*  Modali komponent , annan props, kaasa , samuti ka addEdited(),  mis asub  App.js is */
                onClose={closeModal}
                namePlaceholder={props.name}
                phonePlaceholder={props.phone}
                educationPlaceholder={props.education}
                addEdited={props.addEdited}
                id={props.id}
            />
        </>
    );
};

export default PeopleComponent;
