import React from 'react';
import Card from 'react-bootstrap/Card';
import EditModal from './EditModal';
import { useState } from 'react';


const PeopleComponent = ({ picture, phone, education, name }) => {
    const [like, setLike] = React.useState(true)
    const [isModalOpen, setIsModalOpen] = useState(false);
    const handleLike = () => {
        setLike(prev => !prev)
    }

    const openModal = () => {
        setIsModalOpen(true);
    }

    const closeModal = () => {
        setIsModalOpen(false);
    }


    return (

        <>
            <div className="card" style={{ marginTop: "20px" }}>
                <div style={{ position: 'relative' }}>
                    <img src={picture instanceof File ? URL.createObjectURL(picture) : picture} alt={name} className="card-img-top" />
                    <img src={like ? "./images/like1.jpg" : "./images/like.jpg"}

                        style={{
                            position: 'absolute',
                            bottom: '5px',
                            left: '5px',
                            height: "20px",
                            width: "20px"
                        }}
                        alt="like-icon"
                        onClick={handleLike}
                    />
                    <button onClick={openModal}>Edit</button>
                </div>
                <div className="card-body">
                    <h5 className="card-title">{name}</h5>
                    <p className="card-text">Phone: {phone}</p>
                    <p className="card-text">Education: {education}</p>
                </div>

            </div>
            <EditModal isOpen={isModalOpen} onClose={closeModal} />
        </>
    );
};

export default PeopleComponent;
